document.addEventListener("mousemove",t=>{const o=document.querySelector("[data-background]"),r=document.querySelector("[data-foreground]"),i=window.innerWidth/2,c=window.innerHeight/2,d=(t.clientY-c)/c*15,s=(t.clientX-i)/i*-15;if(o){const n=(t.clientX/window.innerWidth-.5)*10,e=(t.clientY/window.innerHeight-.5)*10;o.style.transform=`translate(${n}px, ${e}px) rotateX(${d}deg) rotateY(${s}deg)`}if(r){const n=(t.clientX/window.innerWidth-.5)*-5,e=(t.clientY/window.innerHeight-.5)*-5;r.style.transform=`translate(${n}px, ${e}px) rotateX(${d}deg) rotateY(${s}deg)`}});
